mapper.update:
update CRM_CUSTOMER_INFO
CUST_NAME = #{custName}, CUST_INDUSTRY = #{custIndustry}, CUST_PROVINCE = #{custProvince}, CUST_CITY = #{custCity}, CUST_ADDRESS = #{custAddress}, CUST_SCALE = #{custScale}, CUST_NATURE = #{custNature}, CUST_INTRODUCE = #{custIntroduce}, CUST_STATE = #{custState}, CUST_CREATE_ID = #{custCreateId}, CUST_CREATE_TIME = #{custCreateTime}, CUST_SUBMIT_ID = #{custSubmitId}, CUST_SUBMIT_TIME = #{custSubmitTime}, CUST_CONFIRM_ID = #{custConfirmId}, CUST_CONFIRM_TIME = #{custConfirmTime}, CUST_PROGRESS_STATE = #{custProgressState}, CUST_LEVEL = #{custLevel}, CUST_COMPANY_WEB = #{custCompanyWeb}, ORG_ID = #{orgId}, CUST_VISIT_AGAIN_TIME = #{custVisitAgainTime},
where CUST_ID = #{custId}
